CREATE TABLE APP_ACT_KMS_CARD (ID CHAR(36) not null default '',  CARDNAME NVARCHAR(128) default NULL,  CARDTYPE SMALLINT default NULL,  CREATEUSER VARCHAR(36) default NULL,  CREATETIME TIMESTAMP default NULL,  LASTUPDATE TIMESTAMP default NULL,  READCOUNT BIGINT default NULL,  ONLINELEVEL SMALLINT default NULL,  SECURITYLEVEL SMALLINT default NULL,  ISPUBLISHED SMALLINT default NULL,  VALIDDATE DATE default NULL,ISCOMMENT SMALLINT default NULL,  constraint "P_Key_KMS_CARD" primary key (ID) );
CREATE TABLE APP_ACT_KMS_DIMENSION (ID CHAR(36) not null default '',  DIMENSIONNAME NVARCHAR(128) default NULL,  PARENTID CHAR(36) default NULL,  SHOWTYPE SMALLINT default NULL,  ISEXAMINE SMALLINT default NULL,  MEMO NVARCHAR(1000) default NULL,  CREATEUSER VARCHAR(36) default NULL, CREATETIME TIMESTAMP default NULL,  LASTUPDATE TIMESTAMP default NULL,  HOTSPOTDEFID CHAR(36) default NULL,  ISENABLED SMALLINT default NULL,  ORDERINDEX INTEGER default NULL,  constraint "P_Key_KMS_DIMENSION" primary key (ID) );
CREATE TABLE APP_ACT_KMS_FILE (ID CHAR(36) not null default '',  CARDID CHAR(36) default NULL,  FILENAME NVARCHAR(128) default NULL,  FILEVER VARCHAR(32) default NULL,  FILESIZE BIGINT default NULL,  FILESTATE SMALLINT default NULL,  CREATETIME TIMESTAMP default NULL,  CREATEUSER VARCHAR(36) default NULL,ISFULLSEARCH SMALLINT default 0, constraint "P_Key_KMS_FILE" primary key (ID) );
create table APP_ACT_KMS_HOTSPOT_DEF ( ID CHAR(36) not null, HOTSPOTNAME NVARCHAR(256) default NULL,HOTSPOTMETAID VARCHAR(36) default NULL, CREATETIME TIMESTAMP default NULL, CREATEUSER VARCHAR(36) default NULL, MEMO NVARCHAR(1000) default NULL,constraint "P_Key_KMS_HOTSPOP_DEF" primary key (ID) );
CREATE TABLE APP_ACT_KMS_HOTSPOT (ID CHAR(36) not null default '', HOTSPOTDEFID CHAR(36) default NULL, SHAPEID VARCHAR(128) default NULL,  DIMENSIONID CHAR(36) default NULL,BINDTYPE SMALLINT default NULL,LINKURL NVARCHAR(128) default NULL,TARGET NVARCHAR(128) default NULL,  constraint "P_Key_KMS_HOTSPOT" primary key (ID) );
CREATE TABLE APP_ACT_KMS_LOG (ID CHAR(36) not null default '',  CARDID CHAR(36) default NULL,  FILEID CHAR(36) default NULL,  ACCESSUSER VARCHAR(36) default NULL,  ACCESSTIME TIMESTAMP default NULL,  IPADDRESS VARCHAR(128) default NULL,  LOGTYPE SMALLINT default NULL,  constraint "P_Key_KMS_LOG" primary key (ID) );
CREATE TABLE APP_ACT_KMS_META_ATTR (ID CHAR(36) not null default '',  SCHEMAID CHAR(36) default NULL,  ATTRTITLE NVARCHAR(80) default NULL,  CREATETIME TIMESTAMP default NULL,  CREATEUSER VARCHAR(36) default NULL,  constraint "P_Key_KMS_META_ATTR" primary key (ID) );
CREATE TABLE APP_ACT_KMS_META_DATA (ID CHAR(36) not null default '',  CARDID CHAR(36) default NULL,  SCHEMAID CHAR(36) default NULL,  ATTRID CHAR(36) default NULL,  CREATETIME TIMESTAMP default NULL,  CREATEUSER VARCHAR(36) default NULL,  METATEXT NVARCHAR(128) default NULL,  constraint "P_Key_KMS_META_DATA" primary key (ID) );
CREATE TABLE APP_ACT_KMS_META_SCHEMA (ID CHAR(36) not null default '',  SCHEMATITLE NVARCHAR(36) default NULL,  SCHEMASHOWTYPE SMALLINT default NULL,  SCHEMADESC NVARCHAR(1000) default NULL,  CREATETIME TIMESTAMP default NULL,  LASTUPDATE TIMESTAMP default NULL,  CREATEUSER VARCHAR(36) default NULL,  ISNULLABLE SMALLINT default NULL,  ISSEARCH SMALLINT default NULL,ORDERINDEX INTEGER default NULL,  constraint "P_Key_KMS_META_SCHEMA" primary key (ID) );
CREATE TABLE APP_ACT_KMS_PUBLISH (ID CHAR(36) not null default '',  CARDID CHAR(36) default NULL,  DIMENSIONID CHAR(36) default NULL,  PUBLISHUSER VARCHAR(36) default NULL,  PUBLISHTIME TIMESTAMP default NULL,  TAG NVARCHAR(256) default NULL,  MEMO NVARCHAR(1000) default NULL,EXAMINEINFO NVARCHAR(100) default NULL,  constraint "P_Key_KMS_PUBLISH" primary key (ID) );
CREATE TABLE APP_ACT_KMS_VERSION (ID CHAR(36) not null default '',  VERSIONNO VARCHAR(80) default NULL,  CREATETIME TIMESTAMP default NULL, CREATEUSER VARCHAR(36) default NULL, MEMO NVARCHAR(1000) default NULL,  constraint "P_Key_KMS_VERSION" primary key (ID) );
CREATE TABLE APP_ACT_KMS_OPT (ID CHAR(36) not null default '', CARDID CHAR(36) default NULL, OPTUSER VARCHAR(36) default NULL, "OPTTYPE" SMALLINT default NULL, OPTTIME TIMESTAMP default NULL,   OPTCONTENT NVARCHAR(1000) default NULL,   constraint "P_Key_KMS_OPT" primary key (ID));