CREATE TABLE APP_ACT_KMS_CARD (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`CARDNAME` varchar(128) DEFAULT NULL COMMENT '卡片名称',`CARDTYPE` smallint(1) DEFAULT NULL COMMENT '卡片类型 0:普通文件?1:图片文件 2:视频文件 3:文本文件 Default(0)',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人UID',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`LASTUPDATE` datetime DEFAULT NULL COMMENT '最后更新时间',`READCOUNT` bigint(12) DEFAULT NULL COMMENT '阅读次数 JAVA LONG类型',`ONLINELEVEL` smallint(1) DEFAULT NULL COMMENT '0:在线阅读 1:在线阅读和打印',`SECURITYLEVEL` smallint(1) DEFAULT NULL COMMENT '安全级别 对应权限组的保密级别\r\n DEFAULT(0)',`ISPUBLISHED` smallint(1) DEFAULT NULL COMMENT '是否已发布 DEFAULT ''0''',`VALIDDATE` date DEFAULT NULL COMMENT '有效日期',`ISCOMMENT` smallint(1) DEFAULT NULL COMMENT '是否可以评论',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_DIMENSION (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`DIMENSIONNAME` varchar(128) DEFAULT NULL COMMENT '维度名称',`PARENTID` char(36) DEFAULT NULL COMMENT '父维度ID',`SHOWTYPE` smallint(1) DEFAULT NULL COMMENT '维度展示类型 0：知识地图 1：列表',`ISEXAMINE` smallint(1) DEFAULT NULL COMMENT '发布是否需要审批 1：需要 0：不需要',`MEMO` varchar(1000) DEFAULT NULL COMMENT '维度描述',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人UID',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`LASTUPDATE` datetime DEFAULT NULL COMMENT '最后修改时间',`HOTSPOTDEFID` char(36) DEFAULT NULL COMMENT '知识地图定义ID SHOWTYPE为0时有效',`ISENABLED` smallint(1) DEFAULT NULL COMMENT '是否启用 1：启用 0：不启用',`ORDERINDEX` int(11) DEFAULT NULL COMMENT '顺序',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_FILE (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`CARDID` char(36) DEFAULT NULL COMMENT '卡片ID',`FILENAME` varchar(128) DEFAULT NULL COMMENT '文件名称',`FILEVER` varchar(32) DEFAULT NULL COMMENT '文件版本号',`FILESIZE` bigint(12) DEFAULT NULL COMMENT '文件大小 单位：(byte)',`FILESTATE` smallint(1) DEFAULT NULL COMMENT '文件状态 0：老版本 1：新版本 2：表单链接',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人UID',`ISFULLSEARCH` smallint(1) DEFAULT '0' COMMENT '是否入库全文检索 1：已入库 0：未入库 2：无需入库',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_HOTSPOT_DEF ( `ID` char(36) NOT NULL COMMENT '主键', `HOTSPOTNAME` varchar(256) DEFAULT NULL COMMENT '知识地图名称',`HOTSPOTMETAID` varchar(36) DEFAULT NULL COMMENT '来自知识地图的metaID', `CREATETIME` datetime DEFAULT NULL COMMENT '创建时间', `CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人', `MEMO` varchar(1000) DEFAULT NULL COMMENT '描述',PRIMARY KEY (`ID`) );
CREATE TABLE APP_ACT_KMS_HOTSPOT (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`HOTSPOTDEFID` char(36) DEFAULT NULL COMMENT '知识地图定义ID',`SHAPEID` varchar(128) DEFAULT NULL COMMENT '热点shapeID',`DIMENSIONID` char(36) DEFAULT NULL COMMENT '维度ID',`BINDTYPE` smallint(1) DEFAULT NULL COMMENT '类型：0 链接 1 维度',`LINKURL` varchar(128) DEFAULT NULL COMMENT '链接',`TARGET` varchar(128) DEFAULT NULL COMMENT '目标',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_LOG (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`CARDID` char(36) DEFAULT NULL COMMENT '卡片ID',`FILEID` char(36) DEFAULT NULL COMMENT '文件ID',`ACCESSUSER` varchar(36) DEFAULT NULL COMMENT '访问者UID',`ACCESSTIME` datetime DEFAULT NULL COMMENT '访问时间',`IPADDRESS` varchar(128) DEFAULT NULL COMMENT 'IP地址',`LOGTYPE` smallint(1) DEFAULT NULL COMMENT '日志类型  0：访问知识卡片 1：下载文件 2：在线浏览文件',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_META_ATTR (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`SCHEMAID` char(36) DEFAULT NULL COMMENT '元数据ID',`ATTRTITLE` varchar(80) DEFAULT NULL COMMENT '属性标题',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人UID',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_META_DATA (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`CARDID` char(36) DEFAULT NULL COMMENT '卡片ID',`SCHEMAID` char(36) DEFAULT NULL COMMENT '元数据ID',`ATTRID` char(36) DEFAULT NULL COMMENT '属性ID',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人UID',`METATEXT` varchar(128) DEFAULT NULL COMMENT '元数据文本内容 SCHEMASHOWTYPE为2(input框)时有效',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_META_SCHEMA (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`SCHEMATITLE` varchar(36) DEFAULT NULL COMMENT '元数据标题',`SCHEMASHOWTYPE` smallint(1) DEFAULT NULL COMMENT '展示类型 0：复选框 1：单选框 2文本框',`SCHEMADESC` varchar(1000) DEFAULT NULL COMMENT '元数据描述',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`LASTUPDATE` datetime DEFAULT NULL COMMENT '最后修改时间',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人UID',`ISNULLABLE` smallint(1) DEFAULT NULL COMMENT '是否可以为空 1：可以 0：不可以',`ISSEARCH` smallint(1) DEFAULT NULL COMMENT '是否参与检索 1：参与 2：不参与',`ORDERINDEX` int(11) DEFAULT NULL COMMENT '顺序',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_PUBLISH (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`CARDID` char(36) DEFAULT NULL COMMENT '卡片ID',`DIMENSIONID` char(36) DEFAULT NULL COMMENT '维度ID',`PUBLISHUSER` varchar(36) DEFAULT NULL COMMENT '发布人UID',`PUBLISHTIME` datetime DEFAULT NULL COMMENT '发布时间',`TAG` varchar(256) DEFAULT NULL COMMENT '标签',`MEMO` varchar(1000) DEFAULT NULL COMMENT '描述',`EXAMINEINFO` varchar(100) DEFAULT NULL COMMENT '发布需要审批时的信息',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_VERSION (`ID` char(36) NOT NULL DEFAULT '' COMMENT '主键 UUID标识，UUIDGener.getUUID()',`VERSIONNO` varchar(80) DEFAULT NULL COMMENT '版本号',`CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',`CREATEUSER` varchar(36) DEFAULT NULL COMMENT '创建人',`MEMO` varchar(1000) DEFAULT NULL COMMENT '描述',PRIMARY KEY (`ID`));
CREATE TABLE APP_ACT_KMS_OPT (`ID` char(36) NOT NULL,`CARDID` char(36) DEFAULT NULL,`OPTUSER` varchar(36) DEFAULT NULL,`OPTTYPE` smallint(1) DEFAULT NULL,`OPTTIME` datetime DEFAULT NULL,`OPTCONTENT` varchar(1000) DEFAULT NULL,PRIMARY KEY (`ID`));