CREATE TABLE APP_ACT_KMS_CARD (ID CHAR(36) default '' not null,  CARDNAME NVARCHAR2(128) default NULL,  CARDTYPE SMALLINT default NULL,  CREATEUSER VARCHAR2(36) default NULL,  CREATETIME DATE default NULL,  LASTUPDATE DATE default NULL,  READCOUNT INTEGER default NULL,  ONLINELEVEL SMALLINT default NULL,  SECURITYLEVEL SMALLINT default NULL,  ISPUBLISHED SMALLINT default NULL,  VALIDDATE DATE default NULL, ISCOMMENT SMALLINT default NULL, constraint PK_APP_ACT_KMS_CARD primary key (ID));
CREATE TABLE APP_ACT_KMS_DIMENSION (ID CHAR(36) default '' not null,  DIMENSIONNAME NVARCHAR2(128) default NULL,  PARENTID CHAR(36) default NULL,  SHOWTYPE SMALLINT default NULL,  ISEXAMINE SMALLINT default NULL,  MEMO NVARCHAR2(1000) default NULL,  CREATEUSER VARCHAR2(36) default NULL, CREATETIME DATE default NULL,  LASTUPDATE DATE default NULL,  HOTSPOTDEFID CHAR(36) default NULL,  ISENABLED SMALLINT default NULL,  ORDERINDEX INTEGER default NULL,  constraint PK_APP_ACT_KMS_DIMENSION primary key (ID));
CREATE TABLE APP_ACT_KMS_FILE (ID CHAR(36) default '' not null,  CARDID CHAR(36) default NULL,  FILENAME NVARCHAR2(128) default NULL,  FILEVER VARCHAR2(32) default NULL,  FILESIZE INTEGER default NULL,  FILESTATE SMALLINT default NULL,  CREATETIME DATE default NULL,  CREATEUSER VARCHAR2(36) default NULL,ISFULLSEARCH SMALLINT default 0,  constraint PK_APP_ACT_KMS_FILE primary key (ID));
create table APP_ACT_KMS_HOTSPOT_DEF  ( ID CHAR(36) not null, HOTSPOTNAME NVARCHAR2(256) default NULL,HOTSPOTMETAID VARCHAR2(36) default NULL, CREATETIME DATE default NULL, CREATEUSER VARCHAR2(36) default NULL,MEMO NVARCHAR2(1000) default NULL, constraint PK_APP_ACT_KMS_HOTSPOT_DEF primary key (ID) );
CREATE TABLE APP_ACT_KMS_HOTSPOT (ID CHAR(36) default '' not null,HOTSPOTDEFID CHAR(36) default NULL,  SHAPEID VARCHAR2(128) default NULL,  DIMENSIONID CHAR(36) default NULL,BINDTYPE SMALLINT default NULL,LINKURL NVARCHAR2(128) default NULL,TARGET NVARCHAR2(128) default NULL,  constraint PK_APP_ACT_KMS_HOTSPOT primary key (ID));
CREATE TABLE APP_ACT_KMS_LOG (ID CHAR(36) default '' not null,  CARDID CHAR(36) default NULL,  FILEID CHAR(36) default NULL,  ACCESSUSER VARCHAR2(36) default NULL,  ACCESSTIME DATE default NULL,  IPADDRESS VARCHAR2(128) default NULL,  LOGTYPE SMALLINT default NULL,  constraint PK_APP_ACT_KMS_LOG primary key (ID));
CREATE TABLE APP_ACT_KMS_META_ATTR (ID CHAR(36) default '' not null,  SCHEMAID CHAR(36) default NULL,  ATTRTITLE NVARCHAR2(80) default NULL,  CREATETIME DATE default NULL,  CREATEUSER VARCHAR2(36) default NULL,  constraint PK_APP_ACT_KMS_META_ATTR primary key (ID));
CREATE TABLE APP_ACT_KMS_META_DATA (ID CHAR(36) default '' not null,  CARDID CHAR(36) default NULL,  SCHEMAID CHAR(36) default NULL,  ATTRID CHAR(36) default NULL,  CREATETIME DATE default NULL,  CREATEUSER VARCHAR2(36) default NULL,  METATEXT NVARCHAR2(128) default NULL,  constraint PK_APP_ACT_KMS_META_DATA primary key (ID));
CREATE TABLE APP_ACT_KMS_META_SCHEMA (ID CHAR(36) default '' not null,  SCHEMATITLE NVARCHAR2(36) default NULL,  SCHEMASHOWTYPE SMALLINT default NULL,  SCHEMADESC NVARCHAR2(1000) default NULL,  CREATETIME DATE default NULL,  LASTUPDATE DATE default NULL,  CREATEUSER VARCHAR2(36) default NULL,  ISNULLABLE SMALLINT default NULL,  ISSEARCH SMALLINT default NULL,ORDERINDEX INTEGER default NULL,  constraint PK_APP_ACT_KMS_META_SCHEMA primary key (ID));
CREATE TABLE APP_ACT_KMS_PUBLISH (ID CHAR(36) default '' not null,  CARDID CHAR(36) default NULL,  DIMENSIONID CHAR(36) default NULL,  PUBLISHUSER VARCHAR2(36) default NULL,  PUBLISHTIME DATE default NULL,  TAG NVARCHAR2(256) default NULL,  MEMO NVARCHAR2(1000) default NULL, EXAMINEINFO NVARCHAR2(100) default NULL, constraint PK_APP_ACT_KMS_PUBLISH primary key (ID));
CREATE TABLE APP_ACT_KMS_VERSION (ID CHAR(36) default '' not null,  VERSIONNO VARCHAR2(80) default NULL,  CREATETIME DATE default NULL,  MEMO NVARCHAR2(1000) default NULL, CREATEUSER VARCHAR2(36) default NULL, constraint PK_APP_ACT_KMS_VERSION primary key (ID));
CREATE TABLE APP_ACT_KMS_OPT (ID CHAR(36) default '' not null, CARDID CHAR(36) default NULL, OPTUSER VARCHAR2(36) default NULL, OPTTYPE SMALLINT default NULL, OPTTIME DATE default NULL, OPTCONTENT NVARCHAR2(1000) default NULL, constraint PK_APP_ACT_KMS_OPT primary key (ID));